import React, { useEffect } from 'react';
import { Carousel } from 'antd';
import './App.css';
import './css/main.css';
const contentStyle = {
  height: '160px',
  color: '#fff',
  lineHeight: '160px',
  textAlign: 'center',
  background: '#364d79',
};
 function App() {
  useEffect(() => {
    const fadeInElements = document.querySelectorAll('.fade-in');
    const fadeOutElements = document.querySelectorAll('.fade-out');
     function handleFadeInsOuts() {
      const currentYPos = window.pageYOffset;
      const screenHeight = window.innerHeight;
      fadeInElements.forEach(fadeIn => {
        const elementTop = fadeIn.getBoundingClientRect().top;
        if (elementTop < screenHeight && elementTop > 0) {
          const opacity = 1 - elementTop / screenHeight;
          fadeIn.style.opacity = opacity;
        }
      });
      fadeOutElements.forEach(fadeOut => {
        const elementTop = fadeOut.getBoundingClientRect().top;
        const elementBottom = fadeOut.getBoundingClientRect().bottom;
        if (elementBottom < screenHeight && currentYPos === 0) {
          const opacity = elementBottom / screenHeight;
          fadeOut.style.opacity = opacity;
        } else {
          fadeOut.style.opacity = 0;
        }
      });
    }
     window.addEventListener('scroll', handleFadeInsOuts);
     return () => {
      window.removeEventListener('scroll', handleFadeInsOuts);
    };
  }, []);
   return (
    <div className="container">
      <div className="about">
        <div className="textZone"></div>
      </div>
      <div className="sideProject">
          <Carousel autoplay>
            <div>
              <h3 style={contentStyle}>1</h3>
            </div>
            <div>
              <h3 style={contentStyle}>2</h3>
            </div>
            <div>
              <h3 style={contentStyle}>3</h3>
            </div>
            <div>
              <h3 style={contentStyle}>4</h3>
            </div>
          </Carousel>
        <div className="projectZone">
          <span className="fade-in">12344455</span>
          {/* <span className="fade-out">12344455</span> */}
        </div>
      </div>
    </div>
  );
}
 export default App;
