import React, { useEffect } from 'react';
import './App.css';
import './css/main.css';
 function App() {
  useEffect(() => {
    const fadeInElements = document.querySelectorAll('.fade-in');
     function handleFadeIns() {
      fadeInElements.forEach(fadeIn => {
        const elementTop = fadeIn.getBoundingClientRect().top;
        const elementBottom = fadeIn.getBoundingClientRect().bottom;
         if (elementTop < window.innerHeight && elementBottom > 0) {
          fadeIn.style.opacity = 1;
        } else {
          fadeIn.style.opacity = 0;
        }
      });
    }
     window.addEventListener('scroll', handleFadeIns);
     return () => {
      window.removeEventListener('scroll', handleFadeIns);
    };
  }, []);
   return (
    <div className="container">
      <div className="about">
        <div className="textZone"></div>
      </div>
      <div className="sideProject">
        <div className="projectZone">
          <span className="fade-in">12344455</span>
        </div>
      </div>
    </div>
  );
}
 export default App;
