/**
 * ```ts
 * const handleMouseMove = throttle(
 *   (ev) => console.log(ev.target.clientX, ev.target.clientY),
 *   50
 * )
 *
 * window.addEventListener('onmousemove', handleMouseMove)
 * ```
 */
export declare function throttle<T extends (...args: any) => any>(func: T, wait: number): (...args: Parameters<T>) => ReturnType<T> | null;
